<div class="modal fade" id="message" role="dialog" style="z-index: 99999" xmlns:margin="http://www.w3.org/1999/xhtml">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h3 class="text-info h3"><span class="glyphicon glyphicon-info-sign"></span>&nbsp;&nbsp;&nbsp;<span                        class="h3"></span></h3>            </div>            <div class="modal-body">                <p></p>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-primary pull-right" data-dismiss="modal" autofocus>Ok</button>            </div>        </div>    </div></div><!-- New Group Modal --><div class="modal fade" id="newGroupModel" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 style="color:red;"><span class="glyphicon glyphicon-user"></span> Add Group</h4>            </div>            <div class="modal-body">                <form role="form" ng-controller="NewGroupController">                    <div class="form-group">                        <label for="groupname"><span class="glyphicon glyphicon-user"></span> Group Name</label>                        <div class="input-group">                            <input type="text" class="form-control" id="groupname" ng-model="groupName" maxlength="256"                                   placeholder="Group Name" aria-describedby="basic-addon2" autofocus>                            <span class="input-group-addon" id="basic-addon2">{{256 - groupName.length}}</span>                        </div>                    </div>                    <button type="submit" class="btn btn-default btn-success btn-block" ng-disabled="!isValidInfo()"                            ng-click="createGroup()">Create Group                    </button>                </form>            </div>        </div>    </div></div><!-- Ask Question Modal --><div class="modal fade" id="askQuestionModel" ng-controller="AskQuestionController" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 style="color:red;"><span class="glyphicon glyphicon-question-sign"></span> Ask Question</h4>            </div>            <div class="modal-body">                <form role="form">                    <div class="form-group">                        <label for="question">Question</label>			  <textarea type="text" class="form-control" id="question" ng-model="question"                        placeholder="Question"></textarea>                    </div>                    <div class="form-group">                        <label for="question-type">Question Type</label>                        <select class="form-control" id="question-type" name="question-type" ng-model="qType"                                ng-options="t[1] for t in questionTypes">                        </select>                    </div>                    <div class="form-group">                        <label for="desc">Description (Optional)</label>			  <textarea type="text" class="form-control" id="desc" ng-model="qDesc"                        placeholder="Description"></textarea>                    </div>                    <button type="submit" class="btn btn-default btn-success btn-block" data-dismiss="modal"                            ng-disabled="!isValid()" ng-click="askQuestion()">Post                    </button>                </form>            </div>        </div>    </div></div><!-- Add Members Modal --><!-- Add Members Modal --><div class="modal fade" id="addMembersModel" ng-controller="AddMembersController" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 style="color:red;"><span class="glyphicon glyphicon-user"></span> {{groupName}}                    <small>Created by {{admin[1]}} ({{admin[0]}})</small>                </h4>            </div>            <ul id="addMemberTab" class="nav nav-tabs">                <li ng-click="currentTab = 0;" class="active"><a href="#specialmember" data-toggle="tab">Special                        Members</a>                </li>                <li ng-click="currentTab = 1;"><a href="#members" data-toggle="tab">Members</a></li>                <li ng-click="currentTab = 2;" ng-show="isAdmin()"><a href="#gsetting" data-toggle="tab">Settings</a>                </li>                <li ng-click="currentTab = 3;"><a href="#grank" data-toggle="tab">Rank</a></li>            </ul>            <div id="addMemberTabContent" class="tab-content scrollbar" style="height: 250px">                <div class="tab-pane fade in active" id="specialmember">                    <div class="modal-body" style="padding-top: 0">                        <table class="table table-responsive table-striped">                            <thead>                            <tr>                                <th ng-if="specialMembers[0].length == 3">                                    <button type="button" class="btn btn-danger btn-sm pull-left"                                            ng-show="hasPermission() || isAdmin()"                                            ng-click="remove()"><span                                            class="glyphicon glyphicon-trash"></span></button>                                </th>                                <th>ID</th>                                <th>Name</th>                            </tr>                            </thead>                            <tbody>                            <tr ng-repeat="a in specialMembers">                                <td ng-if="specialMembers[0].length == 3"><input type="checkbox" ng-model="a[2]"></td>                                <td>{{a[0]}}</td>                                <td>{{a[1]}}</td>                            </tr>                            </tbody>                        </table>                        <div ng-show="hasPermission()">                            <form>                                <label for="addsmemberemail">Knowen ID</label>                                <div class="input-group form-group">                                    <input type="text" placeholder="Knowen ID" id="addsmemberemail" class="form-control"                                           ng-model="email">			  <span class="input-group-btn">				<button type="submit" class="btn btn-primary" ng-disabled="!isValidKnowenId()" ng-click="add()">Add                </button>			  </span>                                </div>                            </form>                        </div>                    </div>                </div>                <div class="tab-pane fade" id="members">                    <div class="modal-body" style="padding-top: 0">                        <table class="table table-responsive table-striped">                            <thead>                            <tr>                                <th ng-if="members[0].length == 3">                                    <button type="button" class="btn btn-danger btn-sm pull-left"                                            ng-show="hasPermission() || isAdmin()"                                            ng-click="remove()"><span                                            class="glyphicon glyphicon-trash"></span></button>                                </th>                                <th>ID</th>                                <th>Name</th>                            </tr>                            </thead>                            <tbody>                            <tr ng-repeat="a in members">                                <td ng-if="members[0].length == 3">                                    <input type="checkbox" ng-model="a[2]">                                </td>                                <td>{{a[0]}}</td>                                <td>{{a[1]}}</td>                            </tr>                            </tbody>                        </table>                        <div ng-show="hasPermission()">                            <form>                                <label for="addmemberemail">Knowen ID</label>                                <div class="input-group form-group">                                    <input type="text" placeholder="Knowen ID" id="addmemberemail" class="form-control"                                           ng-model="email">			  <span class="input-group-btn">				<button type="submit" class="btn btn-primary" ng-disabled="!isValidKnowenId()" ng-click="add()">Add                </button>			  </span>                                </div>                            </form>                        </div>                    </div>                </div>                <div class="tab-pane fade" id="gsetting">                    <div class="modal-body" style="padding-top: 0">                        <label for="renamegrp">Rename Group</label>                        <div class="input-group form-group">                            <input type="text" placeholder="Rename Group" id="renamegrp" class="form-control"                                   ng-model="updateName">			<span class="input-group-btn">			<button type="submit" class="btn btn-primary" ng-disabled="!isValid()" ng-click="rename()">Rename</button>			</span>                        </div>                        <p style="text-align: center"><b>Question Types</b></p>                        <table class="table table-responsive table-striped">                            <thead>                            <tr>                                <th ng-if="questionTypes[0].length == 3">                                    <button type="button" class="btn btn-danger btn-sm pull-left"                                            ng-show="hasPermission() || isAdmin()"                                            ng-click="remove()"><span                                            class="glyphicon glyphicon-trash"></span></button>                                </th>                                <th>Name</th>                            </tr>                            </thead>                            <tbody>                            <tr ng-repeat="a in questionTypes">                                <td><input type="checkbox" ng-model="a[2]"></td>                                <td>{{a[1]}}</td>                            </tr>                            </tbody>                        </table>                        <div class="input-group">                            <input type="text" class="form-control" ng-model="questionType" placeholder="Question Type">				<span class="input-group-btn">				  			<button type="submit" class="btn btn-primary" ng-disabled="!isValidQuestionType()"                                    ng-click="add()">Add                            </button>				</span>                        </div>                    </div>                </div>                <div class="tab-pane fade" id="grank">                    <div class="modal-body" style="padding-top: 0">                        <table class="table table-responsive table-striped">                            <thead>                            <tr>                                <th>Rank</th>                                <th>ID</th>                                <th>Points</th>                            </tr>                            </thead>                            <tbody>                            <tr ng-repeat="(key,value) in ranks">                                <td>{{key + 1}}</td>                                <td>{{value[0]}}</td>                                <td>{{value[1]}}</td>                            </tr>                            </tbody>                        </table>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-primary btn-sm pull-right" data-dismiss="modal">Close</button>                <button type="button" class="btn btn-danger btn-sm pull-left"                        ng-show="isAdmin()"                        ng-click="deleteGroup()"><span                        class="glyphicon glyphicon-trash"></span> Delete Group                </button>            </div>        </div>    </div></div><!-- Setting Modal --><div class="modal fade" id="settingModel" ng-controller="SettingController" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close"                        data-dismiss="modal">&times;</button>                <h4 style="color:red;"><span class="glyphicon glyphicon-user"></span> Settings</h4>            </div>            <div class="modal-body">                <form role="form" ng-controller="SettingController">                        <div class="row">                            <div class="col-sm-6 col-xs-12 col-md-5 col-lg-5 pull-left">                                <div class=" btn-file">                                    <div class="profile-header-container">                                        <div class="profile-header-img">                                            <img class="img-circle" ng-src="{{imageurl}}">                                            <div class="rank-label-container">                                                <a class="label label-default rank-label">Edit Image</a>                                                <input id="avatar-1" name="avatar-1" type="file" accept="image/*"                                                       onchange="angular.element(this).scope().imgUplaod(this)">                                            </div>                                        </div>                                    </div>                                </div>                            </div>                            <div class="col-sm-6 col-xs-12 col-md-5 col-lg-7 ">                                <div style="text-align: center;">                                    <b>User Name</b>                                    <hr style="margin: 0 5px">                                    {{username}}<br><br>                                    <b>Knowen ID</b>                                    <hr style="margin: 0 5px">                                    {{usermail}}<br><br>                                    <p><b>D. O. B.</b>                                    <hr style="margin: 0 5px">                                    {{userdob}}<br><br>                                    </p>                                    <p><b>Country</b>                                    <hr style="margin: 0 5px">                                    {{usercountry}}<br><br>                                    </p>                                </div>                            </div>                    </div>                    <div class="input-group">                        <span class="input-group-addon"><b>Nickname</b></span>                        <input type="text" ng-model="nickname" class="form-control" id="unname"                               onmouseout="this.readOnly=true;" maxlength="256"                               readonly autofocus>                        <span class="input-group-btn">                                <button class="btn btn-secondary glyphicon glyphicon-edit"                                        ng-click="updateInfo('unname')" type="button"></button>                        </span>                    </div>                    <br>                    <div class="input-group">                        <span class="input-group-addon"><b>Mobile No.</b></span>                        <input type="text" ng-model="usermobile" class="form-control"                               onmouseout="this.readOnly=true;"                               id="umob" ng-change="error1=''" maxlength="10"                               readonly autofocus>                        <span class="input-group-btn">                                <button class="btn btn-secondary glyphicon glyphicon-edit" ng-click="updateInfo('umob')"                                        type="button"></button>                             </span>                    </div>                    <div class="text-danger">{{error1}}</div>                    <br>                    <div class="input-group">                        <span class="input-group-addon"><b>Street</b></span>                        <input type="text" class="form-control" id="ustreet"                               onmouseout="this.readOnly=true;" ng-change="error2=''" ng-model="userstreet"                               readonly autofocus>                        <span class="input-group-btn">                                <button class="btn btn-secondary glyphicon glyphicon-edit"                                        ng-click="updateInfo('ustreet')" type="button"></button>                             </span>                    </div>                    <div class="text-danger">{{error2}}</div>                    <br>                    <div class="input-group">                        <span class="input-group-addon"><b>City</b></span>                        <input type="text" class="form-control" id="ucity"                               onmouseout="this.readOnly=true;" ng-change="error3=''" ng-model="usercity"                               readonly autofocus>                        <span class="input-group-btn">                                <button class="btn btn-secondary glyphicon glyphicon-edit"                                        ng-click="updateInfo('ucity')" type="button"></button>                             </span>                    </div>                    <div class="text-danger">{{error3}}</div>                    <br>                    <button type="submit" class="btn btn-default btn-success btn-block" ng-disabled="!isValidInfo()"                            ng-click="update()">Update                    </button>                </form>            </div>        </div>    </div></div><!-- onupdate Modal --><div class="modal fade" id="updateModel" role="dialog">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" onclick="window.location='/home'"                        data-dismiss="modal">&times;</button>                <h2 style="color:green;"><span class="glyphicon glyphicon-ok-circle"></span> Success</h2>            </div>            <div class="modal-body">                <form action="/">                    <div class="form-group">                        <h2 style="text-align: center">You have successfully updated your information</h2>                    </div>                    <button type="submit" class="btn btn-default btn-success btn-block">Ok                    </button>                </form>            </div>        </div>    </div></div>