<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['aid']) || !isset($_POST['comment'])) {  die();}header('Content-Type: application/json');$id = $_SESSION['id'];$aid = $_POST['aid'];$comment = $_POST['comment'];$db = new Database();$result = $db->AddAComment($id, $aid, $comment);if($result === true) {  echo json_encode(array('t' => 's'));} else {  echo json_encode(array('t' => 'e', 'r' => 'Something went wrong'));}