<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['gid']) || !isset($_POST['q']) || !isset($_POST['qt']) || !isset($_POST['qd']) || !isset($_POST['ex'])) {  die();}header('Content-Type: application/json');$id = $_SESSION['id'];$gid = $_POST['gid'];$q = $_POST['q'];$qt = $_POST['qt'];$qd = $_POST['qd'];$expire = $_POST['ex'];$db = new Database();$result = $db->AddQ($id, $gid, $q, $qt, $qd, $expire);if($result === true) {  echo json_encode(array('t' => 's'));} else {  echo json_encode(array('t' => 'e', 'r' => 'Something went wrong'));}