<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['qid']) || !isset($_POST['comment'])) {  die();}header('Content-Type: application/json');$id = $_SESSION['id'];$qid = $_POST['qid'];$comment = $_POST['comment'];$db = new Database();$result = $db->AddQComment($id, $qid, $comment);if($result === true) {  echo json_encode(array('t' => 's', 'r' => 'Comment is added successfully'));} else {  echo json_encode(array('t' => 'e', 'r' => 'Something went wrong'));}