<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['qt']) || !isset($_POST['gid'])) {  die();}//header('Content-Type: application/json');$id = $_SESSION['id'];$gid = $_POST['gid'];$type = $_POST['qt'];$db = new Database();$result = $db->AddQT($id, $gid, $type);if($result === false) {  echo json_encode(array('t' => 'e', 'r' => "Something went wrong"));} else {  echo json_encode(array('t' => 's','r' => $result));}