<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['gid'])) {  die();}header('Content-Type: application/json');$db = new Database();$result = $db->GetAdmin($_SESSION['id'],$_POST['gid']);if($result === false) {  echo json_encode(array('t' => 'e', 'r' => 'Access denied'));} else {  echo json_encode(array('t' => 's', 'r' => $result));}