<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['eid']) || !isset($_POST['gid'])) {  die();}header('Content-Type: application/json');$id = $_SESSION['id'];$eid = $_POST['eid'];$group = $_POST['gid'];$db = new Database();$result = $db->removeM($id, $group, $eid);if($result === true) {  echo json_encode(array('t' => 's'));} else {  echo json_encode(array('t' => 'e', 'r' => $result));}