<?phpinclude_once $_SERVER['DOCUMENT_ROOT'] . '\res\user_manager.inc.php';include_once $_SERVER['DOCUMENT_ROOT'] . '\res\mysqli.inc.php';if(!isLoggedIn()) {  die();}if(!isset($_POST['id']) || !isset($_POST['likes'])) {  die();}header('Content-Type: application/json');$id = $_SESSION['id'];$qId = $_POST['id'];$rank = $_POST['likes'];$db = new Database();$result = $db->SetQuestionRank($id, $qId, $rank);if($result === true) {  echo json_encode(array('t' => 's'));} else {  echo json_encode(array('t' => 'e', 'r' => 'Something went wrong'));}